public class Hadith {    private String text;    private String source;    private String category;    public Hadith(String text, String source, String category) {        this.text = text;        this.source = source;        this.category = category;    }    public String getText() {        return text;    }    public String getSource() {        return source;    }    public String getCategory() {        return category;    }    @Override    public String toString() {        return "Hadith:\n" + text + "\n\nSource:\n" + source + "\n\nCategory:\n" + category + "\n";    }}